def find_gcd(num1, num2):    r = 1    while r != 0 and r > 0:        r = num1 % num2        if r != 0:            num1 = num2            num2 = r    return num2def main():    print("\nThis program will find the greatest common denominator of two integers.\n")    num1 = int(input("enter the first integer: "))    num2 = int(input("enter the second integer: "))    gcd = find_gcd(num1, num2)    print("The greatest common denominator of", num1, "and", num2, "is", gcd)main()